import React, {ChangeEvent, useState} from 'react';
import './App.css';

function App() {
    const [currentValue, setCurrentValue] = useState<number | null>(null);
    const [maxValue, setMaxValue] = useState<number>(5)
    const [startValue, setStartValue] = useState<number>(0)
    const [isSettingMenu, setIsSettingMenu] = useState<boolean>(true)
    const [error, setError] = useState<WithImplicitCoercion<any>>(false)


    const handleStartValueChange = (e: ChangeEvent<HTMLInputElement>) => {
        const value = parseInt(e.currentTarget.value)
        setStartValue(value)
        setError(value <= 0 || value <= maxValue)
    }
    const handleMaxValueChange = (e: ChangeEvent<HTMLInputElement>) => {
        const value = parseInt(e.currentTarget.value, 10)
        setMaxValue(value)
        setError(value < startValue || startValue < 0)
    }
    const handleSet = () => {
        if (!error) {
            setCurrentValue(startValue)
            setIsSettingMenu(false)
        }
    }
    const incHandler = (prev) => {
        setCurrentValue()
    }

    const handleBackToSettings = () => {
        setIsSettingMenu()
    }


    return (
        <div className="App">

            <div className='container'>
                {
                    isSettingMenu
                        ? <div className='display'>
                            <div>
                                {
                                    currentValue !== null
                                        ? <h4>{currentValue}</h4>
                                        : <h4>'Press 'Set' to configure values'</h4>
                                }
                            </div>

                            <div>
                                <button onClick={incHandler}>inc</button>
                                <button>reset</button>
                                <button>set</button>
                            </div>
                        </div>

                        : <div className='boxValue'>
                            <div className="inputs">
                                <div className='input'>
                                    <label>Max Value:</label>
                                    <input
                                        type="number"
                                        value={maxValue}
                                        onChange={handleMaxValueChange}
                                        className={error ? "error" : ""}
                                    />
                                </div>
                                <div className='input'>
                                    <label>Start Value:</label>
                                    <input
                                        type="number"
                                        value={startValue}
                                        onChange={handleStartValueChange}
                                        className={error ? "error" : ""}
                                    />
                                </div>
                            </div>
                            <div className='buttons'>
                                <button onClick={handleSet} disabled={error}>Set</button>
                            </div>
                        </div>
                }


            </div>
        </div>
    );
}

export default App;
