    }


    return (
        <div className="App">

            <div className='container'>
                {
                    isSettingMenu
                        ? <div className='display'>
                            <div>
                                <h3>{currentValue}</h3>
                            </div>

                            <div>
                                <button>inc</button>
                                <button>reset</button>
                                <button>set</button>
                            </div>
                        </div>

                        : <div className='boxValue'>
                            <div className="inputs">
                                <div className='input'>
                                    <label>Max Value:</label>
                                    <input
                                        type="number"
                                        value={maxValue}
                                        onChange={handleMaxValueChange}
                                        className={error ? "error" : ""}
                                    />
                                </div>
                                <div className='input'>
                                    <label>Start Value:</label>
                                    <input
                                        type="number"
                                        value={startValue}
                                        onChange={handleStartValueChange}
                                        className={error ? "error" : ""}
                                    />
                                </div>
                            </div>

